import React, { useState } from 'react';

function UploadForm({ navigate }) {
  const [template, setTemplate] = useState(null);
  const [excel, setExcel] = useState(null);
  const [subject, setSubject] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const form = new FormData();
    form.append('template', template);
    form.append('data', excel);
    form.append('subject', subject);
    form.append('message', message);

    const res = await fetch('http://localhost:5000/upload', {
      method: 'POST',
      body: form,
    });

    if (res.ok) {
      navigate('thankyou');
    } else {
      const result = await res.json();
      alert(result.message || 'Error sending certificates.');
    }
  };

  return (
    <div className="app-container">
      <h1>Upload Details to Generate Certificates</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="template">Upload Certificate Template (PDF)</label>
          <input
            type="file"
            id="template"
            accept="application/pdf"
            onChange={(e) => setTemplate(e.target.files[0])}
            required
          />
          <small>Only PDF format accepted. Ensure placeholders are ready.</small>
        </div>

        <div>
          <label htmlFor="excel">Upload Data File (Excel)</label>
          <input
            type="file"
            id="excel"
            accept=".xlsx,.xls"
            onChange={(e) => setExcel(e.target.files[0])}
            required
          />
          <small>Excel file should contain columns: <b>Name</b>, <b>Email</b>, <b>Course</b>.</small>
        </div>

        <div>
          <label htmlFor="subject">Email Subject</label>
          <input
            type="text"
            id="subject"
            placeholder="e.g. Participation Certificate â€“ Code Camp 2025"
            value={subject}
            onChange={(e) => setSubject(e.target.value)}
            required
          />
        </div>

        <div>
          <label htmlFor="message">Email Body</label>
          <textarea
            id="message"
            placeholder="e.g. Dear participant, please find your certificate attached. Thank you for attending."
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            required
          />
        </div>

        <button type="submit">Generate & Send</button>
      </form>
    </div>
  );
}

export default UploadForm;
